import sys

def main():
    n = int(sys.stdin.readline())
    ans = []
    while n != 0:
        if n % 2 == 0:
            ans.append(0)
            n //= -2
        else:
            ans.append(1)
            n = (n - 1) // -2
    if len(ans) == 0:
        print(0)
    else:
        print(''.join(map(str, ans[::-1])))

if __name__ == '__main__':
    main()

import sys

def main():
    n = int(sys.stdin.readline().rstrip())
    ans = ''
    while n != 0:
        if n % 2 == 0:
            ans = '0' + ans
            n = n // -2
        else:
            ans = '1' + ans
            n = (n - 1) // -2
    if ans == '':
        ans = '0'
    print(ans)

