N = int(input())
H = list(map(int, input().split()))

count = 0
max_count = 0

for i in range(N):
    if i == 0:
        count += 1
    else:
        if H[i] <= H[i - 1]:
            count += 1
        else:
            max_count = max(max_count, count)
            count = 1

max_count = max(max_count, count)
