n, k = map(int, input().split())
a = list(map(int, input().split()))

a.sort()

min_a = a[0]
max_a = a[-1]

if min_a == max_a:
    print(0)
else:
    if k == 1:
        print(1)
    else:
        if k == n:
            print(max_a - min_a)
        else:
            if k <= n // 2:
                print(max_a - min_a)
            else:
                if min_a + (k - n // 2) <= max_a - (k - n // 2):
                    print(max_a - min_a)
                else:
                    print(2 * (k - n // 2) + max_a - min_a)

def main():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))
    a.sort()
    min_val = a[0]
    max_val = a[-1]
    min_count = a.count(min_val)
    max_count = a.count(max_val)
    if min_val == max_val:
        print(0)
    elif min_count >= k or max_count >= k:
        print(1)
    else:
        if min_count + max_count >= k:
            print(2)
        else:
            print(3)

if __name__ == '__main__':
    main()

