n, k = map(int, input().split())
a = list(map(int, input().split()))

min_a = min(a)
max_a = max(a)

if min_a == max_a:
    print(0)
else:
    moves = 0
    while True:
        if len(set(a)) >= k:
            break
        else:
            if min_a < max_a:
                a[a.index(min_a)] += 1
                min_a += 1
            else:
                a[a.index(max_a)] -= 1
                max_a -= 1
            moves += 1
    print(moves)

