import sys

def main():
    n, x = map(int, sys.stdin.readline().split())
    l = list(map(int, sys.stdin.readline().split()))

    # d[i] = i番目の跳ね返りでの位置
    d = [0] * (n + 1)
    for i in range(n):
        d[i + 1] = d[i] + l[i]

    # d[i] <= x となる最大のiを求める
    ans = 0
    for i in range(n + 1):
        if d[i] <= x:
            ans = i

    print(ans)

if __name__ == '__main__':
    main()

import sys

def main():
    n, x = map(int, input().split())
    l = list(map(int, input().split()))

    count = 0
    for i in range(n):
        if x >= l[i]:
            count += 1
            x -= l[i]
        else:
            break
    print(count)

if __name__ == '__main__':
    main()

import sys

def main():
    N, X = map(int, sys.stdin.readline().split())
    L = list(map(int, sys.stdin.readline().split()))
    D = [0]
    for i in range(N):
        D.append(D[i] + L[i])
    print(len([d for d in D if d <= X]))

if __name__ == '__main__':
    main()
