N = int(input())
W = list(map(int, input().split()))

# 二分探索
def binary_search(n, w, left, right):
    if left == right:
        return left
    mid = (left + right) // 2
    if n // 2 <= mid:
        return binary_search(n, w, left, mid)
    else:
        return binary_search(n, w, mid + 1, right)

# 二分探索でTを求める
T = binary_search(N, W, 0, N - 1)

# Tを境に分ける
S1 = sum(W[:T + 1])
S2 = sum(W[T + 1:])

# 絶対値を出力
print(abs(S1 - S2))
