def main():
    n, m = map(int, input().split())
    a = [list(map(int, input().split())) for _ in range(n)]
    ans = 0
    for i in range(n):
        for j in range(m):
            if a[i][j] == (i * m + j + 1):
                continue
            for k in range(i, n):
                if a[k][j] == (i * m + j + 1):
                    for l in range(k, i, -1):
                        a[l][j], a[l - 1][j] = a[l - 1][j], a[l][j]
                        ans += 1
                    break
    print(ans)

if __name__ == '__main__':
    main()

