n = int(input())
a = list(map(int, input().split()))

if n == 2:
    print(abs(a[0] - a[1]))
    exit()

d = {}
for i in a:
    if i in d:
        d[i] += 1
    else:
        d[i] = 1

ans = 0
for i in d:
    ans += d[i] % 2

print(ans)

