def cheese_sort(cheeses):
    soft_cheeses = []
    hard_cheeses = []
    for cheese in cheeses:
        if cheese[1] == 'soft':
            soft_cheeses.append(cheese[0])
        else:
            hard_cheeses.append(cheese[0])
    soft_cheeses.sort()
    hard_cheeses.sort()
    return soft_cheeses + hard_cheeses

def main():
    cheeses = []
    num_cheeses = int(input())
    for i in range(num_cheeses):
        cheeses.append(input().split())
    print(cheese_sort(cheeses))

