import sys

def main():
    # get input
    d, n = map(int, sys.stdin.readline().split())
    # initialize
    counter = 0
    # loop
    for i in range(1, n+1):
        if i % 100 == 0:
            if d == 0:
                counter += 1
            elif d == 1:
                if i % 10000 == 0:
                    counter += 1
            elif d == 2:
                if i % 1000000 == 0:
                    counter += 1
        if counter == n:
            print(i)
            break

if __name__ == '__main__':
    main()

