import sys

def main():
    s = sys.stdin.readline().rstrip()
    k = int(sys.stdin.readline().rstrip())
    print(solve(s, k))

def solve(s, k):
    for i in range(1, 16):
        s = replace(s)
        if len(s) >= k:
            return s[k-1]

def replace(s):
    res = ""
    for c in s:
        res += c * int(c)
    return res

if __name__ == '__main__':
    main()

