def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    b = list(map(int, sys.stdin.readline().split()))

    # d = -b_i / a_i
    d = []
    for i in range(n):
        if a[i] != 0:
            d.append(-b[i] / a[i])
        else:
            d.append(0)

    d.sort()

    # print(d)

    # count the number of zeros
    count = 0
    for i in range(n):
        if a[i] == 0:
            count += 1
        else:
            break

    # print(count)

    # find the maximum number of zeros
    ans = count
    for i in range(n):
        if d[i] == 0:
            continue
        else:
            while i < n and d[i] == d[i + 1]:
                i += 1
            ans = max(ans, count + i + 1)
            count += 1

    print(ans)


