import sys

def solve(a, b):
    n = len(a)
    cnt = 0
    for i in range(n):
        if a[i] == 0:
            if b[i] == 0:
                cnt += 1
            continue
        if b[i] % a[i] == 0:
            cnt += 1
    return cnt

if __name__ == '__main__':
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    b = list(map(int, sys.stdin.readline().split()))
    print(solve(a, b))

