import sys

def main():
    n, m, q = map(int, sys.stdin.readline().split())
    a = [[0] * (m + 1) for _ in range(n + 1)]
    for _ in range(q):
        ai, bi, ci, di = map(int, sys.stdin.readline().split())
        a[bi][ci] += di
        if ai > 0:
            a[ai][ci] -= di
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            a[i][j] += a[i][j - 1]
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            a[i][j] += a[i - 1][j]
    ans = 0
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            ans = max(ans, a[i][j])
    print(ans)

