import math

def main():
    N = int(input())
    ans = 10**10
    for i in range(1, int(math.sqrt(N)) + 1):
        if N % i == 0:
            ans = min(ans, max(len(str(i)), len(str(N // i))))
    print(ans)

if __name__ == '__main__':
    main()

import math

def main():
    N = int(input())
    max_a = math.floor(math.sqrt(N))
    min_f = N
    for a in range(1, max_a+1):
        b = N // a
        if a * b == N:
            f = max(len(str(a)), len(str(b)))
            if f < min_f:
                min_f = f
    print(min_f)

if __name__ == '__main__':
    main()
