import sys

def main():
    n = int(sys.stdin.readline())
    print(n // 2 / n)

if __name__ == '__main__':
    main()

n = int(input())

odd_count = 0

for i in range(1, n + 1):
    if i % 2 == 1:
        odd_count += 1

print(odd_count / n)

N = int(input())

odd = 0

for i in range(1, N+1):
