from collections import deque

n = int(input())
s = input()

slimes = deque()

for i in range(n):
    if len(slimes) == 0:
        slimes.append(s[i])
    else:
        if slimes[-1] == s[i]:
            slimes.pop()
        else:
            slimes.append(s[i])

print(len(slimes))

