def main():
    n = int(input())
    fizz_buzz = [i for i in range(1, n + 1)]
    for i in range(1, n + 1):
        if i % 3 == 0 and i % 5 == 0:
            fizz_buzz[i - 1] = 'FizzBuzz'
        elif i % 3 == 0:
            fizz_buzz[i - 1] = 'Fizz'
        elif i % 5 == 0:
            fizz_buzz[i - 1] = 'Buzz'
    print(sum(i for i in fizz_buzz if type(i) == int))


