import sys

def main():
    n = int(sys.stdin.readline())
    s = sys.stdin.readline()
    if n % 2 == 0:
        for i in range(0, n, 2):
            if s[i] == s[i+1]:
                print(n-2)
                print(s[:i] + s[i+2:])
                return
    else:
        for i in range(0, n-1, 2):
            if s[i] == s[i+1]:
                print(n-1)
                print(s[:i] + s[i+2:])
                return
    print(0)
    print(s)

main()

def main():
    n = int(input())
    s = input()
    i = 0
    while i < n:
        if i + 1 < n and s[i] == s[i + 1]:
            s = s[:i] + s[i + 1:]
            n -= 1
        else:
            i += 1
    print(n)
    print(s)

