import sys

def main():
    n = int(sys.stdin.readline().rstrip())
    ans = 0
    while n > 0:
        if n % 6 == 0:
            n -= 6
            ans += 1
        elif n % 9 == 0:
            n -= 9
            ans += 1
        else:
            n -= 1
            ans += 1
    print(ans)

if __name__ == '__main__':
    main()

import sys

def main():
    n = int(sys.stdin.readline())
    dp = [0] * (n + 1)
    for i in range(1, n + 1):
        dp[i] = dp[i - 1] + 1
        if i % 6 == 0:
            dp[i] = min(dp[i], dp[i // 6] + 1)
        if i % 9 == 0:
            dp[i] = min(dp[i], dp[i // 9] + 1)
    print(dp[n])

if __name__ == '__main__':
    main()

