import sys

def main():
    n, m = map(int, sys.stdin.readline().split())
    a = [int(x) for x in sys.stdin.readline().split()]
    a.sort()
    a.reverse()

    days = 0
    pages = 0
    for i in range(n):
        pages += a[i] - i
        if pages >= m:
            days += 1
            break
    else:
        if pages < m:
            days = -1

    print(days)

if __name__ == '__main__':
    main()

