import sys

def main():
    k = int(sys.stdin.readline().rstrip())
    a, b = map(int, sys.stdin.readline().rstrip().split())
    for i in range(a, b + 1):
        if i % k == 0:
            print("OK")
            return
    print("NG")

if __name__ == '__main__':
    main()

