import math

def main():
    n = int(input())
    ingredients = [int(x) for x in input().split()]
    ingredients.sort()
    for i in range(n-1):
        ingredients[i+1] = (ingredients[i] + ingredients[i+1]) / 2
    print(ingredients[n-1])

if __name__ == '__main__':
    main()

import sys

def main():
    n = int(sys.stdin.readline())
