def main():
    n = int(input())
    a = [int(x) for x in input().split()]
    a.sort()
    ans = 1
    l = 0
    r = 1
    while r < n:
        if a[r] - a[l] <= 5:
            ans = max(ans, r - l + 1)
            r += 1
        else:
            l += 1
    print(ans)

if __name__ == "__main__":
    main()

