def get_max_turns(k, n, a, b):
    if k < a:
        return -1
    elif k < b:
        return n - 1
    else:
        return n - (k - b) // (a - b) - 1

q = int(input())
for _ in range(q):
    k, n, a, b = map(int, input().split())
    print(get_max_turns(k, n, a, b))

