import math

def max_turns(k, n, a, b):
    if k < a:
        return -1
    if k <= b:
        return 0
    if a == b:
        return n
    return math.floor((k-b)/(a-b))

q = int(input())
for i in range(q):
    k, n, a, b = map(int, input().split())
    print(max_turns(k, n, a, b))

