def main():
    n, q = map(int, input().split())
    s = input()
    ac = [0] * (n+1)
    for i in range(n-1):
        if s[i:i+2] == 'AC':
            ac[i+2] = ac[i+1] + 1
        else:
            ac[i+2] = ac[i+1]
    for _ in range(q):
        l, r = map(int, input().split())
        print(ac[r] - ac[l])

if __name__ == '__main__':
    main()
