import sys

def main():
    n, d = map(int, sys.stdin.readline().split())
    print(n // (2 * d + 1) + (n % (2 * d + 1) != 0))

if __name__ == "__main__":
    main()

N, D = map(int, input().split())

if N % (2 * D + 1) == 0:
    print(N // (2 * D + 1))
else:
    print(N // (2 * D + 1) + 1)

import sys

N, D = map(int, sys.stdin.readline().split())

inspectors = 0

for i in range(1, N + 1):
    if i + D <= N:
        inspectors += 1
    else:
        break

print(inspectors)
