def main():
    q = int(input())
    for i in range(q):
        k = int(input())
        print(get_digit(k))

def get_digit(k):
    n = 1
    while k > n:
        k -= n
        n += 1
    return str(n)[k-1]

if __name__ == "__main__":
    main()

# Solution

q = int(input())

for i in range(q):
    k = int(input())
    if k == 1:
        print(1)
    else:
        n = 1
        while k > n * (n + 1) // 2:
            n += 1
        k -= n * (n - 1) // 2
        print(str(n - k + 1)[-1])
