def main():
    n = int(input())
    a = list(map(int, input().split()))
    a.sort()

    if n % 2 == 0:
        if a[0] == a[-1]:
            print(0)
        else:
            print(-1)
    else:
        if a[0] == a[-1]:
            print(0)
        elif a[0] + 1 == a[-1] or a[0] == a[-1] + 1:
            print(1)
        else:
            print(-1)

if __name__ == '__main__':
    main()

import sys

def main():
    n = int(sys.stdin.readline())
    a = [int(x) for x in sys.stdin.readline().split()]

    if n == 1:
        print(0)
        return

    if max(a) - min(a) > sum(a):
        print(-1)
        return

    print(max(a) - min(a))

if __name__ == "__main__":
    main()

import sys
def main():
    n = int(sys.stdin.readline())
    a = list(map(int,sys.stdin.readline().split()))
    print(minD(a))

def minD(a):
    if (len(a) == 1):
        return 0
    a.sort()
    m = a[0]
    for i in range(1,len(a)):
        m = gcd(m,a[i])
    if m == 1:
        return -1
    return m

def gcd(a,b):
    while (b != 0):
        a, b = b, a % b
    return a

if __name__ == "__main__":
    main()

