def main():
    n = int(input().strip())
    arr = list(map(int, input().strip().split(' ')))
    max_val = max(arr)
    min_val = min(arr)
    if arr.count(max_val) == 1:
        print(max_val - min_val)
    else:
        arr.remove(max_val)
        print(max(arr) - min(arr))


if __name__ == "__main__":
    main()

def solve(n, a):
    a.sort()
    if n == 2:
        return a[1] - a[0]
    return min(a[1] - a[0], a[n - 1] - a[n - 2])

if __name__ == '__main__':
    n = int(input())
    a = [int(i) for i in input().split()]
    print(solve(n, a))

n = int(input())
a = list(map(int, input().split()))

mini = min(a)
maxi = max(a)

for i in range(n):
    if a[i] == mini:
        if mini == maxi:
            print(0)
            exit()
        else:
            print(maxi - mini)
            exit()
    elif a[i] == maxi:
        print(maxi - mini)
        exit()
    else:
        if a[i] - mini > maxi - a[i]:
            print(a[i] - mini)
            exit()
        else:
            print(maxi - a[i])
            exit()
