            print("{:02d}:{:02d}".format(start_hour, (start_min+end_min)//2))
        else:
            print("{:02d}:{:02d}".format(start_hour, (start_min+end_min)//2))
    elif end_hour == start_hour+1:
        if start_min == 0 and end_min == 0:
            print("{:02d}:{:02d}".format(start_hour, start_min))
