import sys

def main():
    n = int(sys.stdin.readline())
    q = list(map(int, sys.stdin.readline().split()))
    p = [0] * n
    p[0] = q[0]
    for i in range(1, n-1):
        p[i] = p[i-1] + q[i]
    p[n-1] = p[n-2] + q[n-2]
    if min(p) < 1:
        print(-1)
    else:
        for i in range(n):
            p[i] += i+1
        print(*p)

if __name__ == '__main__':
    main()

n = int(input())
q = [int(x) for x in input().split()]

p = [0] * n
p[0] = q[0] + 1

for i in range(1, n):
    p[i] = p[i-1] + q[i]

if any([x <= 0 or x > n for x in p]):
    print(-1)
else:
    print(' '.join([str(x) for x in p]))
