for i in range(2, N + 1):
    if prime_list[i] == 0:
        for j in range(2 * i, N + 1, i):
            prime_list[j] = 1

# 合成数のリスト
composite_list = []
for i in range(2, N + 1):
    if prime_list[i] == 1:
        composite_list.append(i)

