def length_of_longest_increasing_subarray(a):
    a.append(-1)
    n = len(a)
    dp = [1] * n
    for i in range(1, n):
        if a[i] > a[i - 1]:
            dp[i] = dp[i - 1] + 1
    return max(dp)

print(length_of_longest_increasing_subarray(a))

def find_max_inc_subarray(a):
    n = len(a)
    dp = [1 for i in range(n)]
    for i in range(1, n):
        if a[i] > a[i-1]:
            dp[i] = dp[i-1] + 1
    return max(dp)

def main():
    n = int(input())
    a = list(map(int, input().split()))
    print(find_max_inc_subarray(a))

