import sys


def main():
    N, M = map(int, sys.stdin.readline().split())
    X = list(map(int, sys.stdin.readline().split()))
    X.sort()
    ans = 0
    for i in range(M):
        if X[i] < 0:
            ans += abs(X[i])
            X[i] += abs(X[i])
        elif X[i] > 0:
            ans += abs(X[i] - 1)
            X[i] -= abs(X[i] - 1)
    print(ans)


if __name__ == '__main__':
    main()

def main():
    n, m = map(int, input().split())
    x = list(map(int, input().split()))
    x.sort()
    ans = x[0] - 1
    for i in range(1, m):
        if x[i] - x[i - 1] > 1:
            ans += x[i] - x[i - 1] - 1
    ans += n - x[-1]
    print(ans)


if __name__ == '__main__':
    main()

def main():
    n, m = list(map(int, input().split()))
    x = list(map(int, input().split()))
    x = sorted(x)
    ans = 0
    for i in range(1, m):
        ans += max(0, x[i] - x[i-1] - 1)

    print(ans)

if __name__ == '__main__':
    main()
