def main():
    n, x = map(int, input().split())
    xs = list(map(int, input().split()))
    xs.sort()
    ans = float('inf')
    for i in range(n):
        ans = min(ans, max(x - xs[i], xs[i] - x))
    print(ans)


if __name__ == '__main__':
    main()

n, x = map(int, input().split())
cities = list(map(int, input().split()))
cities.sort()

if cities[0] < x:
    max_dist = x - cities[0]
else:
    max_dist = cities[0] - x

for i in range(1, n):
    dist = cities[i] - cities[i-1]
    if dist > max_dist:
        max_dist = dist

