import heapq

def main():
    n, m = map(int, input().split())
    heap = []
    for i in range(n):
        a, b = map(int, input().split())
        heapq.heappush(heap, (a, b))

    ans = 0
    while m > 0:
        a, b = heapq.heappop(heap)
        if m > b:
            ans += a * b
            m -= b
        else:
            ans += a * m
            break

    print(ans)

if __name__ == "__main__":
    main()

def main():
    N, M = map(int, input().split())
    A_B = []
    for _ in range(N):
        A, B = map(int, input().split())
        A_B.append((A, B))
    A_B.sort()
    ans = 0
    for a, b in A_B:
        if b < M:
            M -= b
            ans += a * b
        else:
            ans += a * M
            break
    print(ans)

if __name__ == '__main__':
    main()
