n = int(input())
arr = list(map(int, input().split()))

memo = [0] * n
memo[0] = 1

for i in range(1, n):
    if arr[i] > arr[i-1]:
        memo[i] = memo[i-1] + 1
    else:
        memo[i] = 1

print(max(memo))

N = int(input())
H = list(map(int, input().split()))

ans = 0
for i in range(N):
    if i == N-1:
        ans += 1
        break
    if H[i+1] > H[i]:
        continue
    else:
        ans += 1
    if H[i+2] > H[i+1]:
        continue
    else:
        ans += 1
        i += 1

print(ans)

def solve():
    n = int(input())
    h = list(map(int, input().split()))
    ans = 0
    for i in range(n):
        if i == n - 1:
            ans += 1
            break
        if h[i] >= h[i + 1]:
            ans += 1
        else:
            ans = 0
    print(ans)


if __name__ == '__main__':
    solve()

