def main():
    n = int(input())
    h = list(map(int, input().split()))
    for i in range(1, n):
        if h[i] < h[i-1]:
            h[i] = h[i-1]
    print("Yes" if all(h[i] <= h[i+1] for i in range(n-1)) else "No")


if __name__ == '__main__':
    main()

import sys

def main():
    n = int(input())
    heights = list(map(int, input().split()))
    for i in range(1, n):
        if heights[i] < heights[i - 1]:
            heights[i] = heights[i - 1]
    print("Yes" if heights == sorted(heights) else "No")

if __name__ == '__main__':
    main()

import sys

n = int(sys.stdin.readline())
heights = [int(x) for x in sys.stdin.readline().split()]

for i in range(1, n):
    if heights[i] < heights[i - 1]:
        heights[i] += 1

if sorted(heights) == heights:
    print('Yes')
else:
    print('No')
