def solve(n, a, b):
    zeroes = 0
    neg = 0
    pos = 0
    for i in range(n):
        if a[i] == 0 and b[i] == 0:
            zeroes += 1
        elif a[i] < 0:
            neg += 1
        elif a[i] > 0:
            pos += 1
    return zeroes + min(neg, pos)

