def dfs(i, honest):
    if i == N:
        return sum(honest)
    honest[i] = True
    if is_honest(i, honest):
        ret0 = dfs(i+1, honest)
    else:
        ret0 = 0
    honest[i] = False
    if is_honest(i, honest):
        ret1 = dfs(i+1, honest)
    else:
        ret1 = 0
    return max(ret0, ret1)

