def main():
    n, l = map(int, input().split())
    apples = [l + i for i in range(n)]
    print(sum(apples) - min(apples))

if __name__ == '__main__':
    main()

N, L = map(int, input().split())
sum = 0
for i in range(N):
    sum += L + i
min = sum
for i in range(N):
    if abs(sum - (L + i)) < min:
        min = abs(sum - (L + i))
print(sum - min)
