import sys

k, a, b = map(int, sys.stdin.readline().split())

if a % k == 0 or b % k == 0 or (a // k) != (b // k):
    print("OK")
else:
    print("NG")

def main():
    k = int(input())
    a, b = map(int, input().split())
    if b//k >= a//k:
        print("OK")
    else:
        print("NG")

if __name__ == "__main__":
    main()

