def main():
    """main function"""
    values = [int(x) for x in input().split()]
    n_people = values[0]
    train_cost = values[1]
    taxi_cost = values[2]

    if train_cost * n_people > taxi_cost:
        print(taxi_cost)
    else:
        print(train_cost * n_people)

if __name__ == '__main__':
    main()

import sys

def main():
    n, a, b = [int(i) for i in sys.stdin.readline().split()]
    if n * a <= b:
        print(n * a)
    else:
        print(b)

if __name__ == '__main__':
    main()

