def main():
    n = int(input())
    x = list(map(int, input().split()))

    idxs = [[] for _ in range(n+1)]
    for i, xi in enumerate(x):
        idxs[xi].append(i)

    min_p = max_p = 0
    for i, idx in enumerate(idxs):
        if len(idx) > 0:
            min_p += 1
            max_p += 1

            if i == 0:
                max_p += len(idx) - 1
            elif i == n:
                max_p += len(idx) - 1
            else:
                max_p += len(idx)

    print(min_p, max_p)


if __name__ == '__main__':
    main()

def get_answer(x):
    x.sort()
    if len(x) == 1:
        return 1, 1

    min_count, max_count = 1, 1

    i = 1
    while i < len(x):
        if x[i] - x[i-1] == 1:
            min_count += 1
            i += 2
        else:
            i += 1

    i = 1
    while i < len(x):
        if x[i] - x[i-1] == 1:
            max_count += 1
            i += 2
        else:
            max_count += 1
            i += 1

    return min_count, max_count


if __name__ == "__main__":
    n = int(input())
    x = list(map(int, input().split()))
    answer = get_answer(x)
    print("{} {}".format(answer[0], answer[1]))
