def solve(n, a):
    a.sort()
    if n == 1:
        return 0
    elif n == 2:
        return abs(a[0]-a[1])
    elif n == 3:
        if a[0] == a[1] or a[1] == a[2]:
            return 0
        else:
            return -1
    else:
        if a[0] == a[n-1]:
            return 0
        elif a[0] == a[1] and a[1] != a[2]:
            return -1
        elif a[0] == a[1] and a[1] == a[2]:
            return solve(n-1, a[1:])
        elif a[0] != a[1] and a[1] != a[2]:
            if (a[0]+a[2])%2 == a[1]%2:
                d1 = (a[1] - a[0])//2
                d2 = (a[2] - a[1])//2
                return min(d1, d2)
            else:
                return -1
        else:
            return -1

