from sys import stdin
from math import gcd

def get_gcd(a, b):
    return gcd(a, b)

def get_min_operations(a, b, c):
    gcd_a_b = get_gcd(a, b)
    gcd_b_c = get_gcd(b, c)

    if gcd_a_b == 1 and gcd_b_c == 1:
        return 1
    elif gcd_a_b == 1 and gcd_b_c > 1:
        return gcd_b_c - b
    elif gcd_a_b > 1 and gcd_b_c == 1:
        return gcd_a_b - a
    elif gcd_a_b > 1 and gcd_b_c > 1:
        return gcd_a_b - a + gcd_b_c - b

def main():
    t = int(stdin.readline())
