    return a//mcd,b//mcd

def solve(a,b,c):
    res = 0
    while b%a or c%b:
        if min(b%a,c%b)<=b-c:
            if c%b:
                c -= 1
                res += 1
            else:
                b -= 1
                res += 1
        else:
            if b%a:
                b += 1
                res += 1
            else:
                c += 1
                res += 1
    return res,a,b,c

