import sys


def read_word(f):
    return next(f).strip()


def read_int(f, b=10):
    return int(read_word(f), b)


def read_words(f, d=' '):
    return read_word(f).split(d)


def read_ints(f, b=10, d=' '):
    return [int(x, b) for x in read_words(f, d)]


def _gcd(a, b):
    while b:
        a, b = b, a % b
    return a


def solve(a, b):
    if a == b:
        return 4 * a
    gcd = _gcd(a, b)
    a //= gcd
    b //= gcd
    return 2 * (a + b) * gcd


def main():
    inp = read_ints(sys.stdin)
    print(solve(inp[0], inp[1]))


if __name__ == '__main__':
    main()

